execution.script:
node('win-10'){
    stage('SCM'){
    git 'https://github.com/ashwinihegde555/DemoApp.git'
    }
    stage('Restoree nuget'){
       bat '"C:/work/nuget.exe" restore DemoApp.sln'
    }
    stage('Clean'){
        bat 'dotnet clean DemoApp.sln'
    }
    //alternative
      /* stage('build'){
        bat "\"${tool 'MSBuild'}\" DemoApp.sln"
    }*/
    stage('Build'){
         bat 'dotnet build DemoApp.sln'
    }
    stage('SonarQube analysis') {
          withSonarQubeEnv() {
            String sonarMSBuild = "C:\\sonar-scanner-msbuild-4.7.1.2311-net46"
            String projectKey     = "anusha"
            bat "\"${sonarMSBuild}/SonarQube.Scanner.MSBuild.exe\" begin /s:${sonarMSBuild}/SonarQube.Analysis.xml  /k:\"${projectKey}\" /v:\"1.0\" "

            bat "\"${tool 'MSBuild'}\"/t:Rebuild  DemoApp.sln "
           bat "\"${sonarMSBuild}/MSBuild.SonarQube.Runner.exe\" end"

          }

    }
    stage('Pack')
    {
        bat 'dotnet pack DemoApp.sln --no-build --output nupkgs'

    }
    stage('Artifactory')
    {
      //withCredentials([usernamePassword(credentialsId: 'artifactory', passwordVariable: 'password', usernameVariable: 'jfrog')]) {
      // bat "dotnet nuget push **\\nupkgs\\*.nupkg -k  X-JFrog-Art-Api:AKCp5ek8EjRmdo8gkoFj72Bqrf5DFGTTLCeYuTHvoiGPyVCu933SKBtTb8E6QakJWwCVTp6J1 -t http://52.38.91.61:8081/artifactory/dotnet/1"
     //bat "dotnet nuget push **\\nupkgs\\*.nupkg -k X-JFrog-Art-Api:AKCp5ek8EjRmdo8gkoFj72Bqrf5DFGTTLCeYuTHvoiGPyVCu933SKBtTb8E6QakJWwCVTp6J1 -s http://52.38.91.61:8081/artifactory/webapp/#/artifacts/browse/tree/General/dotnet"
      def server = Artifactory.newServer url: 'http://52.38.91.61:8081/artifactory', username: 'admin', password: 'admin@123'
        def uploadSpec = """{
  "files": [
            {
                 "pattern": "*",
                 "target": "hars_dotnet/"
             }
         ]  
}"""
